                        break;
                    }
                }
            }
            blackList[removedAddr[index]] = false;
        }
    }

    function getBlacklist() public view onlyOwner returns (address[] memory) {
        return blackLists;
    }

    function setAntiBot(bool _enable) external onlyOwner {
        antiBotEnabled = _enable;
    }

    function transferToken(
        address coinAddress,
        uint256 value,
        address payable to
    ) public onlyOwner {
        require(to != address(0), "ERC20: transfer token the zero address");
        if (coinAddress == address(0)) {
            return to.transfer(value);
        }
        IERC20(coinAddress).transfer(to, value);
    }

    function modifyWhiteListBot(
        address[] calldata newAddr,
        address[] calldata removedAddr
    ) public onlyOwner {
        require(newAddr.length <= 10, "too many address");
        require(removedAddr.length <= 10, "too many address");
        for (uint256 index; index < newAddr.length; index++) {
            if (!whiteListBot[newAddr[index]]) {
                whiteListBots.push(newAddr[index]);
            }
            whiteListBot[newAddr[index]] = true;
        }
        for (uint256 index; index < removedAddr.length; index++) {
            if (whiteListBot[removedAddr[index]]) {
                for (uint256 i = 0; i < whiteListBots.length; i++) {
                    if (whiteListBots[i] == removedAddr[index]) {
                        whiteListBots[i] = whiteListBots[
                            whiteListBots.length - 1
                        ];
                        whiteListBots.pop();
                        break;
                    }
                }
            }
            whiteListBot[removedAddr[index]] = false;
        }
    }

    function getWhiteListBot()
        public
        view
        onlyOwner
        returns (address[] memory)
    {
        return whiteListBots;
    }

    function modifyDexPool(
        address[] calldata newAddr,
        address[] calldata removedAddr
    ) public onlyOwner {
        require(newAddr.length <= 10, "too many address");
        require(removedAddr.length <= 10, "too many address");
        for (uint256 index; index < newAddr.length; index++) {
            if (!dexPool[newAddr[index]]) {
                dexPools.push(newAddr[index]);
            }
            dexPool[newAddr[index]] = true;
        }
        for (uint256 index; index < removedAddr.length; index++) {
            if (dexPool[removedAddr[index]]) {
                for (uint256 i = 0; i < dexPools.length; i++) {
                    if (dexPools[i] == removedAddr[index]) {
                        dexPools[i] = dexPools[dexPools.length - 1];
                        dexPools.pop();
                        break;
                    }
                }
            }
            dexPool[removedAddr[index]] = false;
        }
    }

    function getDexPool() public view onlyOwner returns (address[] memory) {
        return dexPools;
    }

    function changeFeeWallet(address feeWallet) public onlyOwner {
        _feeWallet = feeWallet;
    }

    function changeFee(uint256 feeTransfer) public onlyOwner {
        require(feeTransfer < PERCENTS_DIVIDER, "Invalid feeTransfer rate");
        _feeTransfer = feeTransfer;
    }

    //set the number of tokens required to activate auto-liquidity
    function setNumTokensSell(
        uint256 numTokensSellToAddToLiquidity
    ) public onlyOwner returns (bool) {
        _numTokensSellToAddToLiquidity =
            numTokensSellToAddToLiquidity *
            _decimals;
        return true;
    }

    function setLimitSell(bool _enable) external onlyOwner {
        limitSell = _enable;
    }

    function flush() external onlyOwner {
        uint256 value = address(this).balance;

        if (value == 0) {
            return;
        }

        (bool success, ) = _msgSender().call{value: value}("");
        require(success, "Flush failed");
    }
}
