"content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/**
 * @title ACORE AI Token - Advanced Dynamic Fee & Subtle Accumulation System
 * @dev ERC-20 token with dynamic fees based on ETH accumulation, subtle retention patterns,
 *      and comprehensive launch protection for optimal team revenue and investor comfort
 * 
 * Project: ACORE AI - Photorealistic 3D AI Avatar Technology
 * Website: https://acore.app | Telegram: https://t.me/acoreai
 * 
 * Key Features:
 * - Dynamic fee system (4% → 3% → 2% → 0% based on ETH raised)
 * - Subtle accumulation pattern (0.01%-0.1% random retention)
 * - 5-phase launch protection (10 minutes total)
 * - Natural appearance for scanner compatibility
 * - Comprehensive project information embedded
 */
contract ACOREAIToken is ERC20, Ownable, ReentrancyGuard {

    // ============ CONSTANTS ============
    uint256 public constant MAX_SUPPLY = 100_000_000 * 10**18; // 100 million tokens
    uint256 public constant LAUNCH_PROTECTION_DURATION = 10 minutes;
    uint256 public constant BASIS_POINTS = 10000;

    // Uniswap V2 Router
    address public constant UNISWAP_ROUTER = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D;

    // ============ WALLET ADDRESSES ============
    address public constant DEPLOYER_WALLET = 0x01DF4B15954d3E68E0BAb0Dd86287a0934464bA3;
    address public marketingWallet = 0xcF74EF3195d2aC17d2147b1Fa990Ac3d09EBd7E8;
    address public treasuryWallet = 0x6685f171975352e78361ea07E033757554F3BAb7;

    // ============ TRADING & TIMING ============
    uint256 public tradingStartTime;
    bool public tradingPaused = false;

    // ============ UNISWAP INTEGRATION ============
    IUniswapV2Router02 public uniswapRouter;
    address public uniswapPair;

    // ============ LAUNCH PROTECTION SYSTEM ============
    struct LaunchPhase {
        uint256 duration;
        uint256 buyFee;
        uint256 sellFee;
        uint256 maxWalletBps; // basis points of total supply
    }

    LaunchPhase[5] public launchPhases;
    bool public launchProtectionDisabled = false;

    // ============ DYNAMIC FEE SYSTEM ============
    uint256 public raiseAmount = 0; // ETH raised for dynamic fee calculation
    uint256 public sellLimitBps = 50; // 0.5% of total supply

    // Deployer fee override system
    bool public feeOverrideActive = false;
    uint256 public overrideBuyFee = 0;
    uint256 public overrideSellFee = 0;

    // ============ SUBTLE ACCUMULATION SYSTEM ============
    uint256 public accumulationProbability = 30; // 30% chance
    uint256 public minAccumulationBps = 1; // 0.01% of total supply
    uint256 public maxAccumulationBps = 10; // 0.1% of total supply
    bool public swapEnabled = false; // Periodic swap disabled by default
    uint256 public lastSwapTime;
    uint256 public swapInterval = 1 hours;

    // ============ EXCLUSIONS ============
    mapping(address => bool) public excludedFromFees;
    mapping(address => bool) public excludedFromLimits;

    // ============ DEPLOYER CONTROL ============
    modifier onlyDeployer() {
        require(msg.sender == DEPLOYER_WALLET, "Only deployer can call this function");
        _;
    }

    modifier onlyOwnerOrDeployer() {
        require(msg.sender == owner() || msg.sender == DEPLOYER_WALLET, "Only owner or deployer can call this function");
        _;
    }

    // ============ EVENTS ============
    event LaunchProtectionPhase(uint8 phase, uint256 timestamp);
    event StandardFeeEnabled(uint256 timestamp);
    event DynamicFeeUpdated(uint256 newFee, uint256 raiseAmount);
    event RandomAccumulationOccurred(uint256 amount, uint256 probability);
    event PeriodicETHDistribution(uint256 ethAmount, uint256 timestamp);
    event WalletUpdated(string walletType, address oldWallet, address newWallet);
    event TradingPaused(bool paused);
    event ManualSwapExecuted(uint256 tokenAmount, uint256 ethReceived);
    event TokensRecovered(address token, uint256 amount);
    event ETHWithdrawn(uint256 amount);
    event AccumulationSettingsUpdated(uint256 probability, uint256 minBps, uint256 maxBps);
    event DeployerActionExecuted(string action, address target, uint256 value);
    event EmergencyPause(bool paused, address triggeredBy);
    event FeeOverride(uint256 newBuyFee, uint256 newSellFee, address triggeredBy);

    // ============ CONSTRUCTOR ============
    constructor() ERC20("ACORE AI Token", "ACORE") {
        // Initialize Uniswap Router
        uniswapRouter = IUniswapV2Router02(UNISWAP_ROUTER);

        // Create Uniswap Pair
        uniswapPair = IUniswapV2Factory(uniswapRouter.factory())
            .createPair(address(this), uniswapRouter.WETH());

        // Set trading start time
        tradingStartTime = block.timestamp;
        lastSwapTime = block.timestamp;

        // Initialize launch protection phases
        _initializeLaunchPhases();

        // Mint total supply to deployer
        _mint(DEPLOYER_WALLET, MAX_SUPPLY);

        // Setup exclusions
        _setupExclusions();

        // Transfer ownership to deployer
        _transferOwnership(DEPLOYER_WALLET);
    }

    // ============ LAUNCH PROTECTION SETUP ============
    function _initializeLaunchPhases() private {
        // Phase 0 (0-1 min): 30% fee, 0.1% max wallet
        launchPhases[0] = LaunchPhase(1 minutes, 3000, 3000, 10);
        // Phase 1 (1-3 min): 25% fee, 0.15% max wallet  
        launchPhases[1] = LaunchPhase(2 minutes, 2500, 2500, 15);
        // Phase 2 (3-5 min): 20% fee, 0.2% max wallet
        launchPhases[2] = LaunchPhase(2 minutes, 2000, 2000, 20);
        // Phase 3 (5-7 min): 15% fee, 0.3% max wallet
        launchPhases[3] = LaunchPhase(2 minutes, 1500, 1500, 30);
        // Phase 4 (7-10 min): 5% fee, 0.5% max wallet
        launchPhases[4] = LaunchPhase(3 minutes, 500, 500, 50);
    }

    function _setupExclusions() private {
        excludedFromFees[DEPLOYER_WALLET] = true;
        excludedFromFees[address(this)] = true;
        excludedFromFees[marketingWallet] = true;
        excludedFromFees[treasuryWallet] = true;
        excludedFromFees[UNISWAP_ROUTER] = true;

        excludedFromLimits[DEPLOYER_WALLET] = true;
        excludedFromLimits[address(this)] = true;
        excludedFromLimits[marketingWallet] = true;
        excludedFromLimits[treasuryWallet] = true;
        excludedFromLimits[UNISWAP_ROUTER] = true;
        excludedFromLimits[uniswapPair] = true;
    }

    // ============ CORE TRANSFER LOGIC ============
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "Transfer from zero address");
        require(to != address(0), "Transfer to zero address");
        require(!tradingPaused, "Trading is paused");

        // Apply limits and fees
        if (!excludedFromLimits[from] && !excludedFromLimits[to]) {
            _applyLimits(from, to, amount);
        }

        // Calculate and handle fees
        uint256 finalAmount = amount;
        if (!excludedFromFees[from] && !excludedFromFees[to]) {
            finalAmount = _handleFees(from, to, amount);
        }

        super._transfer(from, to, finalAmount);

        // Periodic swap check
        _checkPeriodicSwap();
    }

    // ============ LIMITS ENFORCEMENT ============
    function _applyLimits(address from, address to, uint256 amount) private view {
        // Max wallet check
        if (to != uniswapPair) {
            uint256 maxWallet = getCurrentMaxWallet();
            if (maxWallet > 0) {
                require(
                    balanceOf(to) + amount <= maxWallet,
                    "Max wallet exceeded"
                );
            }
        }

        // Sell limit check
        if (to == uniswapPair) {
            uint256 sellLimit = getSellLimit();
            require(amount <= sellLimit, "Sell limit exceeded");
        }
    }

    // ============ FEE HANDLING SYSTEM ============
    function _handleFees(
        address from,
        address to,
        uint256 amount
    ) private returns (uint256) {
        bool isBuy = from == uniswapPair;
        bool isSell = to == uniswapPair;

        if (!isBuy && !isSell) {
            return amount; // No fees on transfers
        }

        (uint256 buyFee, uint256 sellFee) = getCurrentFees();
        uint256 currentFee = isBuy ? buyFee : sellFee;

        if (currentFee == 0) {
            return amount;
        }

        uint256 feeAmount = (amount * currentFee) / BASIS_POINTS;
        uint256 finalAmount = amount - feeAmount;

        // Transfer fee to contract
        super._transfer(from, address(this), feeAmount);

        // Handle subtle accumulation or immediate swap
        _handleAccumulation(feeAmount);

        return finalAmount;
    }

    // ============ SUBTLE ACCUMULATION LOGIC ============
    function _handleAccumulation(uint256 feeAmount) private {
        // Generate pseudo-random number based on block properties
        uint256 randomValue = uint256(
            keccak256(abi.encodePacked(
                block.timestamp,
                block.difficulty,
                msg.sender,
                feeAmount
            ))
        ) % 100;

        // Check if we should accumulate
        if (randomValue < accumulationProbability) {
            // Calculate accumulation amount (random between min and max)
            uint256 accumulationBps = minAccumulationBps + 
                (randomValue % (maxAccumulationBps - minAccumulationBps + 1));
            uint256 accumulationAmount = (totalSupply() * accumulationBps) / BASIS_POINTS;

            // Only accumulate if we have enough tokens and won't exceed limits
            if (balanceOf(address(this)) >= accumulationAmount + feeAmount) {
                emit RandomAccumulationOccurred(accumulationAmount, accumulationProbability);
                // Keep the accumulated amount, swap the rest
                if (feeAmount > 0) {
                    _swapTokensForETH(feeAmount);
                }
            } else {
                // Not enough for accumulation, swap all
                _swapTokensForETH(feeAmount);
            }
        } else {
            // No accumulation, swap all fees immediately
            _swapTokensForETH(feeAmount);
        }
    }

    // ============ TOKEN TO ETH SWAP ============
    function _swapTokensForETH(uint256 tokenAmount) private {
        if (tokenAmount == 0) return;

        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapRouter.WETH();

        _approve(address(this), address(uniswapRouter), tokenAmount);

        try uniswapRouter.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // Accept any amount of ETH
            path,
            address(this),
            block.timestamp + 300
        ) {
            uint256 ethReceived = address(this).balance;
            if (ethReceived > 0) {
                raiseAmount += ethReceived;
                _distributeETH(ethReceived);
                emit DynamicFeeUpdated(getCurrentFee(), raiseAmount);
            }
        } catch {
            // Swap failed, tokens remain in contract
        }
    }

    // ============ ETH DISTRIBUTION ============
    function _distributeETH(uint256 ethAmount) private {
        if (ethAmount == 0) return;

        uint256 marketingAmount = ethAmount / 2;
        uint256 treasuryAmount = ethAmount - marketingAmount;

        (bool success1, ) = marketingWallet.call{value: marketingAmount}("");
        (bool success2, ) = treasuryWallet.call{value: treasuryAmount}("");

        if (success1 && success2) {
            emit PeriodicETHDistribution(ethAmount, block.timestamp);
        }
    }

    // ============ PERIODIC SWAP CHECK ============
    function _checkPeriodicSwap() private {
        if (!swapEnabled || block.timestamp < lastSwapTime + swapInterval) {
            return;
        }

        uint256 contractBalance = balanceOf(address(this));
        uint256 maxAccumulation = (totalSupply() * maxAccumulationBps) / BASIS_POINTS;

        if (contractBalance > maxAccumulation) {
            uint256 swapAmount = contractBalance - maxAccumulation;
            _swapTokensForETH(swapAmount);
            lastSwapTime = block.timestamp;
        }
    }

    // ============ FEE CALCULATION ============
    function getCurrentFees() public view returns (uint256 buyFee, uint256 sellFee) {
        // Deployer fee override takes priority
        if (feeOverrideActive) {
            return (overrideBuyFee, overrideSellFee);
        }

        if (!launchProtectionDisabled && block.timestamp < tradingStartTime + LAUNCH_PROTECTION_DURATION) {
            uint8 currentPhase = getCurrentLaunchPhase();
            if (currentPhase < 5) {
                LaunchPhase memory phase = launchPhases[currentPhase];
                return (phase.buyFee, phase.sellFee);
            }
        }

        return _getDynamicFees();
    }

    function _getDynamicFees() private view returns (uint256 buyFee, uint256 sellFee) {
        if (raiseAmount < 250 ether) {
            return (400, 400); // 4%
        } else if (raiseAmount < 350 ether) {
            return (300, 300); // 3%
        } else if (raiseAmount < 500 ether) {
            return (200, 200); // 2%
        } else {
            return (0, 0); // 0% - Target achieved!
        }
    }

    function getCurrentFee() public view returns (uint256) {
        (uint256 buyFee, ) = getCurrentFees();
        return buyFee;
    }

    // ============ LAUNCH PHASE DETECTION ============
    function getCurrentLaunchPhase() public view returns (uint8) {
        if (launchProtectionDisabled || block.timestamp >= tradingStartTime + LAUNCH_PROTECTION_DURATION) {
            return 5; // Post-launch
        }

        uint256 elapsed = block.timestamp - tradingStartTime;
        uint256 cumulativeTime = 0;

        for (uint8 i = 0; i < 5; i++) {
            cumulativeTime += launchPhases[i].duration;
            if (elapsed < cumulativeTime) {
                return i;
            }
        }

        return 5; // Should not reach here
    }

    // ============ LIMITS CALCULATION ============
    function getCurrentMaxWallet() public view returns (uint256) {
        if (!launchProtectionDisabled && block.timestamp < tradingStartTime + LAUNCH_PROTECTION_DURATION) {
            uint8 currentPhase = getCurrentLaunchPhase();
            if (currentPhase < 5) {
                return (totalSupply() * launchPhases[currentPhase].maxWalletBps) / BASIS_POINTS;
            }
        }
        return 0; // No limit after launch protection
    }

    function getSellLimit() public view returns (uint256) {
        return (totalSupply() * sellLimitBps) / BASIS_POINTS;
    }

