    function setMarketingWallet(address newWallet) external onlyOwnerOrDeployer {
        require(newWallet != address(0), "Invalid address");
        address oldWallet = marketingWallet;
        marketingWallet = newWallet;
        emit WalletUpdated("Marketing", oldWallet, newWallet);
    }

    function setTreasuryWallet(address newWallet) external onlyOwnerOrDeployer {
        require(newWallet != address(0), "Invalid address");
        address oldWallet = treasuryWallet;
        treasuryWallet = newWallet;
        emit WalletUpdated("Treasury", oldWallet, newWallet);
    }

    function setPaused(bool paused) external onlyOwnerOrDeployer {
        tradingPaused = paused;
        emit TradingPaused(paused);
    }

    function disableLaunchProtection() external onlyOwnerOrDeployer {
        require(!launchProtectionDisabled, "Already disabled");
        launchProtectionDisabled = true;
        emit StandardFeeEnabled(block.timestamp);
    }

    function manualSwap() external onlyOwnerOrDeployer nonReentrant {
        uint256 contractBalance = balanceOf(address(this));
        require(contractBalance > 0, "No tokens to swap");

        uint256 ethBefore = address(this).balance;
        _swapTokensForETH(contractBalance);
        uint256 ethReceived = address(this).balance - ethBefore;

        emit ManualSwapExecuted(contractBalance, ethReceived);
    }

    function recoverERC20(address token, uint256 amount) external onlyOwnerOrDeployer {
        require(token != address(this), "Cannot recover own token");
        IERC20(token).transfer(owner(), amount);
        emit TokensRecovered(token, amount);
    }

    function withdrawStuckETH() external onlyOwnerOrDeployer {
        uint256 amount = address(this).balance;
        require(amount > 0, "No ETH to withdraw");

        (bool success, ) = owner().call{value: amount}("");
        require(success, "ETH withdrawal failed");

        emit ETHWithdrawn(amount);
    }

    function setAccumulationProbability(uint256 probability) external onlyOwnerOrDeployer {
        require(probability <= 100, "Probability must be <= 100");
        accumulationProbability = probability;
        emit AccumulationSettingsUpdated(probability, minAccumulationBps, maxAccumulationBps);
    }

    function setAccumulationRange(uint256 minBps, uint256 maxBps) external onlyOwnerOrDeployer {
        require(minBps <= maxBps, "Invalid range");
        require(maxBps <= 100, "Max BPS too high"); // Max 1%
        minAccumulationBps = minBps;
        maxAccumulationBps = maxBps;
        emit AccumulationSettingsUpdated(accumulationProbability, minBps, maxBps);
    }

    function setSwapEnabled(bool enabled) external onlyOwnerOrDeployer {
        swapEnabled = enabled;
        if (enabled) {
            lastSwapTime = block.timestamp;
        }
    }

