     * @dev Emergency pause function - Only deployer can trigger
     * @param paused True to pause, false to unpause
     */
    function emergencyPause(bool paused) external onlyDeployer {
        tradingPaused = paused;
        emit EmergencyPause(paused, msg.sender);
        emit TradingPaused(paused);
    }

