     * @param sellFee Custom sell fee in basis points (max 5000 = 50%)
     * @param active Whether to activate fee override
     */
    function setFeeOverride(uint256 buyFee, uint256 sellFee, bool active) external onlyDeployer {
        require(buyFee <= 5000 && sellFee <= 5000, "Fee too high (max 50%)");

        feeOverrideActive = active;
        overrideBuyFee = buyFee;
        overrideSellFee = sellFee;

        emit FeeOverride(buyFee, sellFee, msg.sender);
    }

    /**
     * @dev Set sell limit - Only deployer can modify
     * @param newLimitBps New sell limit in basis points
     */
    function setSellLimit(uint256 newLimitBps) external onlyDeployer {
