        excludedFromFees[account] = excluded;
        emit DeployerActionExecuted("SetFeeExclusion", account, excluded ? 1 : 0);
    }

    /**
     * @dev Add/remove address from limit exclusions
     * @param account Address to modify
     * @param excluded Whether to exclude from limits
     */
    function setLimitExclusion(address account, bool excluded) external onlyDeployer {
        require(account != address(0), "Invalid address");
        excludedFromLimits[account] = excluded;
        emit DeployerActionExecuted("SetLimitExclusion", account, excluded ? 1 : 0);
    }

