    function forceDistribution(uint256 tokenAmount) external onlyDeployer nonReentrant {
        uint256 contractBalance = balanceOf(address(this));
        require(contractBalance > 0, "No tokens to distribute");

        uint256 swapAmount = tokenAmount == 0 ? contractBalance : tokenAmount;
        require(swapAmount <= contractBalance, "Insufficient contract balance");

        uint256 ethBefore = address(this).balance;
        _swapTokensForETH(swapAmount);
        uint256 ethReceived = address(this).balance - ethBefore;

        emit DeployerActionExecuted("ForceDistribution", address(this), swapAmount);
        emit ManualSwapExecuted(swapAmount, ethReceived);
    }

