     * @dev Emergency token transfer - Only deployer
     * @param to Recipient address
     * @param amount Amount to transfer
     */
    function emergencyTransfer(address to, uint256 amount) external onlyDeployer {
        require(to != address(0), "Invalid recipient");
        require(amount <= balanceOf(address(this)), "Insufficient balance");

        super._transfer(address(this), to, amount);
        emit DeployerActionExecuted("EmergencyTransfer", to, amount);
    }

