     * @dev Get deployer control status and settings
     */
    function getDeployerControlInfo() external view returns (
        address deployerAddress,
        bool feeOverrideStatus,
        uint256 currentOverrideBuyFee,
        uint256 currentOverrideSellFee,
        uint256 currentSellLimit,
        uint256 currentRaiseAmount
    ) {
        return (
            DEPLOYER_WALLET,
            feeOverrideActive,
            overrideBuyFee,
            overrideSellFee,
            sellLimitBps,
            raiseAmount
        );
    }

    // ============ VIEW FUNCTIONS ============
    function getLaunchProtectionInfo() external view returns (
        bool active,
        uint8 currentPhase,
        uint256 timeRemaining,
        uint256 currentFee,
        uint256 currentMaxWallet
    ) {
        active = !launchProtectionDisabled && block.timestamp < tradingStartTime + LAUNCH_PROTECTION_DURATION;
        currentPhase = getCurrentLaunchPhase();

        if (active && currentPhase < 5) {
            uint256 phaseEndTime = tradingStartTime;
            for (uint8 i = 0; i <= currentPhase; i++) {
                phaseEndTime += launchPhases[i].duration;
            }
            timeRemaining = phaseEndTime > block.timestamp ? phaseEndTime - block.timestamp : 0;
        }

        (currentFee, ) = getCurrentFees();
        currentMaxWallet = getCurrentMaxWallet();
    }

    function getDynamicFeeInfo() external view returns (
        uint256 currentBuyFee,
        uint256 currentSellFee,
        uint256 currentRaiseAmount,
        uint256 nextThreshold,
        uint256 nextFee
    ) {
        (currentBuyFee, currentSellFee) = getCurrentFees();
        currentRaiseAmount = raiseAmount;

        if (raiseAmount < 250 ether) {
            nextThreshold = 250 ether;
            nextFee = 300; // 3%
        } else if (raiseAmount < 350 ether) {
            nextThreshold = 350 ether;
            nextFee = 200; // 2%
        } else if (raiseAmount < 500 ether) {
            nextThreshold = 500 ether;
            nextFee = 0; // 0%
        } else {
            nextThreshold = 0; // Target achieved
            nextFee = 0;
        }
    }

    function getTokenInfo() external pure returns (
        string memory name,
        string memory symbol,
        uint8 decimals,
        uint256 totalSupply
    ) {
        return ("ACORE AI Token", "ACORE", 18, MAX_SUPPLY);
    }

    function getContractStatus() external view returns (
        bool tradingActive,
        bool launchProtectionActive,
        bool swapActive,
        uint256 contractETHBalance,
        uint256 contractTokenBalance,
        uint256 lastSwap
    ) {
        tradingActive = !tradingPaused;
        launchProtectionActive = !launchProtectionDisabled && 
            block.timestamp < tradingStartTime + LAUNCH_PROTECTION_DURATION;
        swapActive = swapEnabled;
        contractETHBalance = address(this).balance;
        contractTokenBalance = balanceOf(address(this));
        lastSwap = lastSwapTime;
    }

    function getProjectDescription() external pure returns (string memory) {
        return "ACORE AI represents a paradigm shift in virtual agent technology, combining cutting-edge artificial intelligence with photorealistic 3D human avatars that can speak, interact, and react with unprecedented human-like authenticity. Our platform enables organizations to deploy sophisticated virtual agents across multiple channels while maintaining consistent, intelligent, and engaging user experiences.";
    }

    function getProjectLinks() external pure returns (
        string memory website,
        string memory telegram,
        string memory twitter,
        string memory medium,
        string memory linkedin,
        string memory crunchbase
    ) {
        return (
            "https://acore.app",
            "https://t.me/acoreai",
            "https://x.com/acore_ai",
            "https://medium.com/@acoreai",
            "https://linkedin.com/company/acoreai",
            "https://www.crunchbase.com/organization/acore-ai"
        );
    }

    function getAccumulationSettings() external view returns (
        uint256 probability,
        uint256 minBps,
        uint256 maxBps,
        bool periodicSwapEnabled,
        uint256 swapIntervalSeconds
    ) {
        return (
            accumulationProbability,
            minAccumulationBps,
            maxAccumulationBps,
            swapEnabled,
            swapInterval
        );
    }

    // ============ RECEIVE ETH ============
    receive() external payable {
        // Accept ETH from swaps
    }
}

// ============ INTERFACES ============
interface IUniswapV2Factory {
    function createPair(address tokenA, address tokenB) external returns (address pair);
}

interface IUniswapV2Router02 {
    function factory() external pure returns (address);
    function WETH() external pure returns (address);

    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
}
