function staxMigrate(uint amount) external {
        require(block.chainid == 56, "IF V1: Only can migrate tokens from STAX V1 contract on BSC");
        IERC20 staxContract = IERC20(0x0Da6Ed8B13214Ff28e9Ca979Dd37439e8a88F6c4);
        require(staxContract.transferFrom(msg.sender, address(this), amount), "IF V1: STAX contract transferFrom failed");
        _mint(msg.sender, amount);
    }

function _mint(address account, uint256 amount) internal {
        require(account != address(0), "ERC20: mint to the zero address");

        _totalSupply += amount;
        balanceOf[account] += amount;
        emit Transfer(address(0), account, amount);
    }
