// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";

contract TesseractAI is ERC20, Ownable {
    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;

    address public marketingWallet;
    address public devWallet;

    uint256 public maxTransactionAmount;
    uint256 public maxWallet;
    uint256 public swapTokensAtAmount;

    bool public swapEnabled = true;
    bool private swapping;

    mapping(address => bool) private _isExcludedFromFees;
    mapping(address => bool) public _isBlacklisted;

    uint256 public buyDevTax;
    uint256 public buyMarketingTax;
    uint256 public sellDevTax;
    uint256 public sellMarketingTax;

    event ExcludeFromFees(address indexed account, bool isExcluded);
    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);
    event UpdateUniswapV2Router(address indexed newAddress, address indexed oldAddress);
    event SwapAndDistributeTaxes(uint256 tokensSwapped, uint256 ethReceived, uint256 devPortion, uint256 marketingPortion);
    event SwapEnabledUpdated(bool enabled);
    event TaxesUpdated(uint256 buyDevTax, uint256 buyMarketingTax, uint256 sellDevTax, uint256 sellMarketingTax);
    event ManualSwap(uint256 tokensSwapped, uint256 ethReceived, uint256 devPortion, uint256 marketingPortion);
    event LimitsRemoved();
    event MarketingWalletUpdated(address indexed newWallet);
    event DevWalletUpdated(address indexed newWallet);
    event MaxTransactionAmountUpdated(uint256 newAmount);
    event MaxWalletUpdated(uint256 newAmount);
    event SwapTokensAtAmountUpdated(uint256 newAmount);

    constructor() ERC20("TesseractAI", "TSAI") {
        uint256 totalSupply = 1000000000 * 10**decimals();

        marketingWallet = 0xfe08E5241f04b7446420097aFD664bCb86Fe8fa7;
        devWallet = 0x9e4087C48f3495F6e7cF72E532b9196a10999D42;

        maxTransactionAmount = totalSupply * 1 / 100;
        maxWallet = totalSupply * 1 / 100;
        swapTokensAtAmount = 500000 * 10**decimals();

        buyDevTax = 2;
        buyMarketingTax = 3;
        sellDevTax = 2;
        sellMarketingTax = 3;

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory()).createPair(address(this), _uniswapV2Router.WETH());
        uniswapV2Router = _uniswapV2Router;

        _approve(address(this), address(uniswapV2Router), type(uint256).max);

        _isExcludedFromFees[owner()] = true;
        _isExcludedFromFees[address(this)] = true;
        _isExcludedFromFees[marketingWallet] = true;
        _isExcludedFromFees[devWallet] = true;

        _mint(owner(), totalSupply);
    }

    receive() external payable {}

    function setMarketingWallet(address _marketingWallet) external onlyOwner {
        require(_marketingWallet != address(0), "Marketing wallet cannot be zero address");
        marketingWallet = _marketingWallet;
        emit MarketingWalletUpdated(_marketingWallet);
    }

    function setDevWallet(address _devWallet) external onlyOwner {
        require(_devWallet != address(0), "Dev wallet cannot be zero address");
        devWallet = _devWallet;
        emit DevWalletUpdated(_devWallet);
    }

    function setOwnerWallet(address _newOwner) external onlyOwner {
        transferOwnership(_newOwner);
    }

    function setTaxes(
        uint256 _buyDevTax,
        uint256 _buyMarketingTax,
        uint256 _sellDevTax,
        uint256 _sellMarketingTax
    ) external onlyOwner {
        require(_buyDevTax + _buyMarketingTax <= 50, "Buy taxes cannot exceed 50%");
        require(_sellDevTax + _sellMarketingTax <= 50, "Sell taxes cannot exceed 50%");

        buyDevTax = _buyDevTax;
        buyMarketingTax = _buyMarketingTax;
        sellDevTax = _sellDevTax;
        sellMarketingTax = _sellMarketingTax;

        emit TaxesUpdated(_buyDevTax, _buyMarketingTax, _sellDevTax, _sellMarketingTax);
    }

    function setMaxTransactionAmount(uint256 _maxTxAmount) external onlyOwner {
        maxTransactionAmount = _maxTxAmount;
        emit MaxTransactionAmountUpdated(_maxTxAmount);
    }

    function setMaxWallet(uint256 _maxWallet) external onlyOwner {
        maxWallet = _maxWallet;
        emit MaxWalletUpdated(_maxWallet);
    }

    function removeMaxTransactionLimit() external onlyOwner {
        maxTransactionAmount = totalSupply();
        emit MaxTransactionAmountUpdated(totalSupply());
    }

    function removeMaxWalletLimit() external onlyOwner {
        maxWallet = totalSupply();
        emit MaxWalletUpdated(totalSupply());
    }

    function removeLimits() external onlyOwner {
        maxTransactionAmount = totalSupply();
        maxWallet = totalSupply();
        emit LimitsRemoved();
    }

    function setBlacklistStatus(address account, bool blacklisted) external onlyOwner {
        _isBlacklisted[account] = blacklisted;
    }

    function excludeFromFees(address account, bool excluded) external onlyOwner {
        _isExcludedFromFees[account] = excluded;
        emit ExcludeFromFees(account, excluded);
    }

    function includeInFees(address account) external onlyOwner {
        _isExcludedFromFees[account] = false;
    }

    function setSwapTokensAtAmount(uint256 _swapTokensAtAmount) external onlyOwner {
        swapTokensAtAmount = _swapTokensAtAmount;
        emit SwapTokensAtAmountUpdated(_swapTokensAtAmount);
    }

    function manualSwap() external onlyOwner {
        uint256 contractTokenBalance = balanceOf(address(this));
        require(contractTokenBalance > 0, "No tokens to swap");
        swapAndDistributeTaxes(contractTokenBalance);
    }

    function setSwapEnabled(bool _enabled) external onlyOwner {
        swapEnabled = _enabled;
        emit SwapEnabledUpdated(_enabled);
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal override {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        require(!_isBlacklisted[sender] && !_isBlacklisted[recipient], "Blacklisted address");

        if (amount == 0) {
            super._transfer(sender, recipient, 0);
            return;
        }

        if (sender != owner() && recipient != owner() && sender != address(this) && recipient != address(this) && sender != marketingWallet && recipient != marketingWallet && sender != devWallet && recipient != devWallet) {
            require(amount <= maxTransactionAmount, "Transfer amount exceeds the maxTxAmount.");
        }

        uint256 contractTokenBalance = balanceOf(address(this)); 
        bool canSwap = contractTokenBalance >= swapTokensAtAmount;

        if (
            canSwap &&
            !swapping &&
            swapEnabled &&
            sender != uniswapV2Pair &&
            !_isExcludedFromFees[sender] &&
            !_isExcludedFromFees[recipient]
        ) {
            try this.swapAndDistributeTaxes(swapTokensAtAmount) {
            } catch {
            }
        }

        bool takeFee = !swapping;

        if (_isExcludedFromFees[sender] || _isExcludedFromFees[recipient]) {
            takeFee = false;
        }

        if (takeFee) {
            uint256 fees = 0;
            if (sender == uniswapV2Pair) {
                require(swapEnabled || sender == owner() || recipient == owner(), "Buying is currently disabled");
                fees = amount * (buyDevTax + buyMarketingTax) / 100;
            } else if (recipient == uniswapV2Pair) {
                require(swapEnabled || sender == owner() || recipient == owner(), "Sell is currently disabled");
                fees = amount * (sellDevTax + sellMarketingTax) / 100;
            }

            if (fees > 0) {
                super._transfer(sender, address(this), fees);
                amount = amount - fees;
            }
        }

        super._transfer(sender, recipient, amount);

        if (!_isExcludedFromFees[recipient] && recipient != owner() && recipient != uniswapV2Pair && recipient != address(this) && recipient != marketingWallet && recipient != devWallet) {
            require(balanceOf(recipient) <= maxWallet, "Exceeds maximum wallet token amount");
        }
    }

    function swapAndDistributeTaxes(uint256 tokens) public {
        require(msg.sender == address(this), "Only contract can call");
        swapping = true;

        uint256 contractTokenBalance = balanceOf(address(this));
        uint256 tokensToSwap = contractTokenBalance >= tokens ? contractTokenBalance : tokens;

        uint256 initialBalance = address(this).balance;
        swapTokensForEth(tokensToSwap);
        uint256 newBalance = address(this).balance - initialBalance;

        uint256 totalTax = buyDevTax + buyMarketingTax;
        uint256 devPortion = newBalance * buyDevTax / totalTax;
        uint256 marketingPortion = newBalance - devPortion;

        payable(devWallet).transfer(devPortion);
        payable(marketingWallet).transfer(marketingPortion);

        emit SwapAndDistributeTaxes(tokensToSwap, newBalance, devPortion, marketingPortion);

        swapping = false;
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(this),
            block.timestamp
        );
    }
}
"

