    "contracts/WDIONEBridged.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.18;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable2Step.sol";

import {DioneBridge} from "./bridge/DioneBridge.sol";

//////////////////////////////////////////////////
///                                            ///
///            Wrapped Dione token             ///
///                                            ///
//////////////////////////////////////////////////

contract WDIONEBridged is ERC20, Ownable2Step{
    /// ERRORS

    error FeeTooBig();
    error ZeroAddress();
    error OnlyBridge();
    error BalanceUnderflow();

    /// EVENTS

    event FeesSentToBridge(address indexed receiver, uint256 amount);
    event FeeUpdated(uint256 newFee);
    event FeeReceiverUpdated(address newFeeReceiver);
    event PayFeeListUpdated(address account, bool isPayFee);
    event FeeThresholdUpdated(uint256 newFeeThreshold);

    /// CONSTANTS

    /// @notice Divisor for computation (1 bps (basis point) precision: 0.001%).
    uint32 constant PCT_DIV = 100_000; 
    /// @notice Minimum amount of accumulated commission to send commission to Odyssey
    uint256 constant MIN_FEE_THRESHOLD = 1 * 10 ** 18;
    /// @notice Dione bridge instance
    DioneBridge immutable public BRIDGE;
    /// @notice Odyssey chain id, used in Wanchain gateway
    uint256 immutable public ODYSSEY_ID;
    
    /// STORAGE

    /// @notice List of addresses of token recipients for which commission is charged
    mapping(address => bool) public isPayFee;
    /// @notice send fees to the Odyssey chain if collected fees are above this threshold
    uint256 public feeThreshold;
    /// @notice collected fees tracker
    uint256 public collectedFees;
    /// @notice transfer fee in bps, [0...100000]
    uint32 public fee;
    /// @notice account that will receive bridged fees in Odyssey
    address public feeReceiver;
    /// @dev Indicator that an attempt was made to send fees during the transfer
    bool private _isTrySendFees;

    modifier onlyBridge() {
        if(msg.sender != address(BRIDGE)) revert OnlyBridge();
        _;
    }
    /// @notice Set initial parameters for the token
    /// @param name Token name
    /// @param symbol Token symbol
    /// @param _bridge Dione bridge address
    /// @param _targetId chain Id of the fee receiver
    /// @param _owner token admin
    /// @param _fee fee amount in bps [0...100000]
    /// @param _feeThreshold send fees to the receiver if collected amount is above the threshold
    /// @param _feeReceiver address of the fee receiver in the target chain
    /// @param _toAdd array of (accounts)token recipients for which commission is charged
    constructor(
        string memory name,
        string memory symbol,
        DioneBridge _bridge,
        uint256 _targetId,
        address _owner,
        uint32 _fee,
        uint256 _feeThreshold,
        address _feeReceiver,
        address[] memory _toAdd
    )
        ERC20(name, symbol)
    {
        if (address(_bridge) == address(0)) revert ZeroAddress();
        if(fee > PCT_DIV) revert FeeTooBig();
        BRIDGE = _bridge;
        ODYSSEY_ID = _targetId;
        fee = _fee;
        feeThreshold = _feeThreshold;
        feeReceiver = _feeReceiver;
        setPayFeeListBatch(_toAdd, true);

        _transferOwnership(_owner);
    }

    /// @notice Send collected fees to the target chain if threshold condition allows
    /// @dev Will not revert in failure
    /// @dev This contract MUST have ETH tokens to pay for the tokens bridging into the target chain
    function checkAndSendFees() public {
        if (!_isTrySendFees) {
            if(collectedFees > feeThreshold) {
                _isTrySendFees = true;
                uint256 _collectedFees = collectedFees;
                collectedFees = 0;

                uint256 toPay = _estimateGas();
                if(address(this).balance < toPay) return;

                _approve(address(this), address(BRIDGE), _collectedFees);
                bool success = _trySendFees(toPay);
                _approve(address(this), address(BRIDGE), 0);
                
                if(!success) {
                    collectedFees = _collectedFees;
                    return;
                }
                emit FeesSentToBridge(feeReceiver, _collectedFees);
            }
        }
    }

    receive() external payable {}

    ///------------------ BRIDGE ------------------///

    /// @notice Mint tokens to the recipient
    /// @param to Minted tokens recipient
    /// @param amount Minted tokens amount
    function mint(address to, uint256 amount) onlyBridge external {
        _mint(to, amount);
    }

    /// @notice Burn tokens from the account
    /// @param amount Burned tokens amount
    function burn(uint256 amount) onlyBridge external {
        if(balanceOf(msg.sender) < amount) revert BalanceUnderflow();
        _burn(msg.sender, amount);
    }

    function _trySendFees(uint256 toPay) internal returns(bool success) {
        (success, ) = address(BRIDGE).call{value: toPay}(abi.encodeWithSelector(
            BRIDGE.redeem.selector,
            feeReceiver,
            ODYSSEY_ID,
            IERC20(address(this)),
            collectedFees
        ));
    } 

    function _estimateGas() internal view returns(uint256 toPay) {
        toPay = BRIDGE.estimateFee(
            ODYSSEY_ID,
            BRIDGE.messageGasLimit()
        );
    }

    ///------------------ ADMIN ------------------///

    /// @notice Set fee 
    /// @param _fee fee amount in bps [0...100000]
    function setFee(uint32 _fee) onlyOwner external {
        if(_fee > PCT_DIV) revert FeeTooBig();
        fee = _fee;

        emit FeeUpdated(_fee);
    }

    /// @notice Set fee receiver
    /// @param _feeReceiver address of the fee receiver in the target chain
    function setFeeReceiver(address _feeReceiver) onlyOwner external {
        if (_feeReceiver == address(0)) revert ZeroAddress();
        feeReceiver = _feeReceiver;

        emit FeeReceiverUpdated(_feeReceiver);
    }

    /// @notice Set fee threshold
    /// @param _feeThreshold send fees to the receiver if collected amount is above the threshold
    function setFeeThreshold(uint256 _feeThreshold) onlyOwner external {
        require(_feeThreshold >= MIN_FEE_THRESHOLD, "WDIONEBridged: Fee threshold too low");
        feeThreshold = _feeThreshold;

        emit FeeThresholdUpdated(_feeThreshold);
    }

    /// @notice Configure the pay fee list
    /// @param account Account to add/remove from the pay fee list
    /// @param add Add=true, Remove=false
    function setPayFeeList(address account, bool add) onlyOwner public {
        if (account == address(0)) revert ZeroAddress();
        isPayFee[account] = add;

        emit PayFeeListUpdated(account, add);
    }

    /// @notice Mass configure the pay fee list
    /// @param accounts Array of accounts
    /// @param add Add=true, Remove=false
    function setPayFeeListBatch(address[] memory accounts, bool add) onlyOwner public {
        for(uint256 i=0; i<accounts.length; ++i) {
            if (accounts[i] == address(0)) revert ZeroAddress();
            isPayFee[accounts[i]] = add;

            emit PayFeeListUpdated(accounts[i], add);
        }
    }

    /// @notice Withdraw native tokens from contract
    /// @param amount Amount of native tokens
    function withdrawNative(uint256 amount) onlyOwner external {
        (bool success, ) = msg.sender.call{value: amount}("");
        require(success, "ETH_TRANSFER_FAILED");
    }

    ///------------------ ERC20 ------------------///

    function transferFrom(address from, address to, uint256 amount) public override returns (bool) {
        address spender = _msgSender();
        uint256 feeAmount = 0;
        if(isPayFee[to] && to != address(BRIDGE)) {
            feeAmount = amount * fee / PCT_DIV;
        }
        if (spender != from) {
            _spendAllowance(from, spender, amount);
        }
        if (feeAmount > 0) {
            collectedFees += feeAmount;
            _transfer(from, address(this), feeAmount);
        }
        _transfer(from, to, amount - feeAmount);

        _isTrySendFees = false;
        return true;
    }

    function transfer(address to, uint256 amount) public override returns (bool) {
        address owner = _msgSender();
        return transferFrom(owner, to, amount);
    }

    function _afterTokenTransfer(address, address, uint256) internal override {
        checkAndSendFees();
    }
}


"
    },
    "contracts/bridge/DioneBridge.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.18;

import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "./interfaces/IWETH9.sol";
import "../wanchain/app/WmbAppUpgradeable.sol";

interface IERC20Mintable is IERC20 {
    function mint(address to, uint256 amount) external;
}

contract DioneBridge is Initializable, WmbAppUpgradeable, ReentrancyGuardUpgradeable, PausableUpgradeable {
    using SafeERC20 for IERC20;
    using SafeERC20 for IERC20Mintable;
    using SafeERC20 for ERC20Burnable;

    enum Operation {
        Invalid,
        Withdraw,
        Mint,
        Deposit,
        Redeem
    }

    bytes32 public constant GOVERNANCE_ROLE = keccak256("GOVERNANCE_ROLE");
    bytes32 public constant RESCUER_ROLE = keccak256("RESCUER_ROLE");

    uint256 public startBlockNumber;
    uint256 public constant bridgeVersion = 1;
    address payable public WETH_ADDRESS;
    uint256 public messageGasLimit;
    uint256 public feeInBp;
    uint256 public constant percentConverter = 1e4;

    mapping(bytes32 => mapping(address => uint256)) public pendingRoles;
    mapping(address => uint256) private _wrappedTokenMaxSupply;
    mapping(uint256 => mapping(address => address)) private _supportedTokensForReceive;
    mapping(uint256 => mapping(address => Operation)) private _allowedTokenOperations;
    mapping(uint256 => address) private _supportedBridges;
    mapping(address => bool) private _isTokenSupportedToSend;
    mapping(address => uint256) private _accumulatedFees;
    mapping(address => uint256) private _balances;
    mapping(address => bool) public isFeeFree;
    mapping(address => uint256) public minBridgedAmounts;
    mapping(address => mapping(address => uint256)) public emergencyTokenStorage;

    receive() external payable {}

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        address _wmbGateway
    ) external initializer {
        require(_wmbGateway != address(0), "Invalid wmbGateway address");
        startBlockNumber = block.number;
        __AccessControlDefaultAdminRules_init(0, msg.sender);
        _setupRole(RESCUER_ROLE, msg.sender);
        _setupRole(GOVERNANCE_ROLE, msg.sender);
        _setupWmbGateway(_wmbGateway);

        messageGasLimit = 160000;
    }

    function setWethAddress(address payable _wethAddress) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Not admin");
        require(_wethAddress != address(0), "Invalid WETH address");
        WETH_ADDRESS = _wethAddress;

        emit WethAddressUpdated(_wethAddress);
    }

    function setSupportedBridge(uint256 chainId, address _bridgeAddress) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Not admin");
        require(_bridgeAddress != address(0), "Invalid bridge address");
        _supportedBridges[chainId] = _bridgeAddress;

        emit SupportedBridgeUpdated(chainId, _bridgeAddress);
    }

    function setMaxSupply(address dioneERC20, uint256 maxSupply) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Not admin");
        require(dioneERC20 != address(0), "Invalid dione ERC20 address");
        _wrappedTokenMaxSupply[dioneERC20] = maxSupply;

        emit WrappedTokenMaxSupplyUpdated(dioneERC20, maxSupply);
    }

    function setMessageGasLimit(uint256 gasLimit) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Not admin");
        require(gasLimit >= IWmbGateway(wmbGateway).minGasLimit(), "Gas limit too low");
        require(gasLimit <= IWmbGateway(wmbGateway).maxGasLimit(), "Gas limit too high");
        messageGasLimit = gasLimit;

        emit MessageGasLimitUpdated(gasLimit);
    }

    function setTokenForReceive(
        uint256 fromChainId,
        address fromToken,
        address toToken,
        Operation allowedOp
    ) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Not admin");
        require(fromToken != address(0), "Invalid fromToken address");
        require(toToken != address(0), "Invalid toToken address");
        
        _supportedTokensForReceive[fromChainId][fromToken] = toToken;
        _allowedTokenOperations[fromChainId][fromToken] = allowedOp;

        emit SupportedTokenForReceiveUpdated(fromChainId, fromToken, toToken, allowedOp);
    }

    function setTokenToSend(
        address token,
        bool isSupported,
        Operation allowedOp
    ) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Not admin");
        require(token != address(0), "Invalid token address");
        uint256 chainId = IWmbGateway(wmbGateway).chainId();
        
        _isTokenSupportedToSend[token] = isSupported;
        _allowedTokenOperations[chainId][token] = allowedOp;

        emit SupportedTokenForSendUpdated(token, isSupported, allowedOp);
    }

    function setFee(uint256 _fee) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Not admin");
        require(_fee <= percentConverter, "Invalid fee");

        feeInBp = _fee;

        emit FeeUpdated(_fee);
    }

    function setFeeFreelist(address account, bool add) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Not admin");
        require(account != address(0), "Invalid account address");

        isFeeFree[account] = add;

        emit FeeFreeListUpdated(account, add);
    }

    function setMinBridgedAmounts(address token, uint256 amount) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Not admin");
        require(token != address(0), "Invalid token address");

        minBridgedAmounts[token] = amount;

        emit MinBridgetAmountsUpdated(token, amount);
    }

    event TokenDeposit(address indexed to, uint256 chainId, IERC20 token, uint256 amount, uint256 feeAmount);
    event TokenRedeem(address indexed to, uint256 chainId, IERC20 token, uint256 amount, uint256 feeAmount);
    event TokenWithdraw(address indexed to, IERC20 token, uint256 amount, uint256 feeInBp, bytes32 indexed messageId);
    event TokenMint(address indexed to, IERC20Mintable token, uint256 amount, uint256 feeInBp, bytes32 indexed messageId);
    event TokenAddedToEmergencyTokenStorage(address indexed account, address indexed token, uint256 amount);

    event WethAddressUpdated(address newWethAddress);
    event SupportedBridgeUpdated(uint256 chainId, address newBridgeAddress);
    event WrappedTokenMaxSupplyUpdated(address token, uint256 newMaxSupply);
    event MessageGasLimitUpdated(uint256 newGasLimit);
    event SupportedTokenForReceiveUpdated(uint256 fromChainId, address fromToken, address toToken, Operation allowedOp);
    event SupportedTokenForSendUpdated(address token, bool isSupported, Operation allowedOp);
    event FeeUpdated(uint256 fee);
    event FeeFreeListUpdated(address account, bool isFeeFree);
    event MinBridgetAmountsUpdated(address token, uint256 amount);

    // VIEW FUNCTIONS ***/

    function getWrappedTokenMaxSupply(address dioneERC20) public view returns (uint256) {
        return _wrappedTokenMaxSupply[dioneERC20] == 0 ? type(uint256).max : _wrappedTokenMaxSupply[dioneERC20];
    }

    function getSupportedTokensForReceive(uint256 fromChainId, address fromToken) external view returns(address) {
        return _supportedTokensForReceive[fromChainId][fromToken];
    }

    function getSupportedBridges(uint256 chainId) external view returns(address) {
        return _supportedBridges[chainId];
    }

    function getIsTokenSupportedToSend(address token) external view returns(bool) {
        return _isTokenSupportedToSend[token];
    }

    function getAllowedTokenOperations(uint256 chainId, address token) external view returns(Operation) {
        return _allowedTokenOperations[chainId][token];
    }

    // PAUSABLE FUNCTIONS ***/
    function pause() external {
        require(hasRole(GOVERNANCE_ROLE, msg.sender), "Not governance");
        _pause();
    }

    function unpause() external {
        require(hasRole(GOVERNANCE_ROLE, msg.sender), "Not governance");
        _unpause();
    }

    /**
     * @notice Relays to nodes to transfers an ERC20 token cross-chain
     * @param to address on other chain to bridge assets to
     * @param chainId which chain to bridge assets onto
     * @param token ERC20 compatible token to deposit into the bridge
     * @param amount Amount in native token decimals to transfer cross-chain pre-fees
     **/
    function deposit(
        address to,
        uint256 chainId,
        IERC20 token,
        uint256 amount
    ) external payable nonReentrant whenNotPaused {
        address tokenAddress = address(token) == address(0) ? WETH_ADDRESS : address(token);
        require(_isTokenSupportedToSend[tokenAddress], "Unsupported token");
        uint256 curChainId = IWmbGateway(wmbGateway).chainId();
        require(_allowedTokenOperations[curChainId][tokenAddress] == Operation.Deposit, "Unsupported operation");
        uint256 messageFee = estimateFee(chainId, messageGasLimit);
        require(amount >= minBridgedAmounts[tokenAddress], "Amount of tokens does not exceed the minimum bridged value");

        uint256 recievedAmount = 0;
        uint256 receivedMessageFee = 0;
        if (address(token) == address(0)) {
            require(msg.value >= (messageFee + amount), "Insufficient amount or message feeInBp");

            IWETH9(WETH_ADDRESS).deposit{value: amount}();
            recievedAmount = amount;
            receivedMessageFee = msg.value - amount;
        } else {
            require(msg.value >= messageFee, "Insufficient message feeInBp");
            uint256 prevBalance = token.balanceOf(address(this));

            token.safeTransferFrom(msg.sender, address(this), amount);
            recievedAmount = token.balanceOf(address(this)) - prevBalance;
            receivedMessageFee = msg.value;
        }

        uint256 feeAmount = _calcBridgeFee(recievedAmount);
        _balances[tokenAddress] += recievedAmount - feeAmount;
        _accumulatedFees[tokenAddress] += feeAmount;
        emit TokenDeposit(to, chainId, token, recievedAmount - feeAmount, feeAmount);

        if (receivedMessageFee - messageFee > 0) {
            (bool success, ) = msg.sender.call{value: receivedMessageFee - messageFee}("");
            require(success, "ETH_TRANSFER_FAILED");
        }

        bytes memory data = _encodeMessageData(
            to,
            tokenAddress,
            recievedAmount - feeAmount,
            Operation.Mint
        );
        _sendMessage(chainId, data, messageFee);
    }

    /**
     * @notice Relays to nodes that (typically) a wrapped dioneAsset ERC20 token has been burned and the underlying needs to be redeeemed on the native chain
     * @param to address on other chain to redeem underlying assets to
     * @param chainId which underlying chain to bridge assets onto
     * @param token ERC20 compatible token to deposit into the bridge
     * @param amount Amount in native token decimals to transfer cross-chain pre-fees
     **/
    function redeem(
        address to,
        uint256 chainId,
        ERC20Burnable token,
        uint256 amount
    ) external payable nonReentrant whenNotPaused {
        require(_isTokenSupportedToSend[address(token)], "Unsupported token");
        uint256 curChainId = IWmbGateway(wmbGateway).chainId();
        require(_allowedTokenOperations[curChainId][address(token)] == Operation.Redeem, "Unsupported operation");
        uint256 messageFee = estimateFee(chainId, messageGasLimit);
        require(msg.value >= messageFee, "Insufficient message feeInBp");
        require(amount >= minBridgedAmounts[address(token)], "Amount of tokens does not exceed the minimum bridged value");
        uint256 prevBalance = token.balanceOf(address(this));

        token.safeTransferFrom(msg.sender, address(this), amount);
        uint256 recievedAmount = token.balanceOf(address(this)) - prevBalance;
        uint256 feeAmount = _calcBridgeFee(recievedAmount);

        _accumulatedFees[address(token)] += feeAmount;
        emit TokenRedeem(to, chainId, token, recievedAmount - feeAmount, feeAmount);
        token.burn(recievedAmount - feeAmount);

        if (msg.value - messageFee > 0) {
            (bool success, ) = msg.sender.call{value: msg.value - messageFee}("");
            require(success, "ETH_TRANSFER_FAILED");
        }

        bytes memory data = _encodeMessageData(to, address(token), recievedAmount - feeAmount, Operation.Withdraw);
        _sendMessage(chainId, data, messageFee);
    }

    function withdrawFromEmergencyStorage(address token, uint256 amount) external {
        address owner = msg.sender;
        require(emergencyTokenStorage[owner][token] >= amount, "Amount exceeds the tokens stored in the storage");
        
        emergencyTokenStorage[owner][token] -= amount;
        if (token == address(0)) {
            (bool success, ) = owner.call{value: amount}("");
            require(success, "ETH_TRANSFER_FAILED");
        } else {
            IERC20(token).safeTransfer(owner, amount);
        }
    }

    /**
     * @notice Rescues tokens that are sent to the contract by mistake and cannot be retrieved by other means
     * USE WITH EXTREME CAUTION - CAN BREAK THE ACCOUNTING OF THE BRIDGE
     * @param token ERC20 compatible token to retrieve from the bridge
     * @param to address of the receiver
     * @param amount amount in token decimals to transfer
     **/
    function rescueTokens(
        IERC20 token,
        address to,
        uint256 amount
    ) external nonReentrant {
        require(hasRole(RESCUER_ROLE, msg.sender), "Not rescuer");
        uint256 contractBalance = address(token) == address(0) ? address(this).balance : token.balanceOf(address(this));
        require(contractBalance - (_balances[address(token)] + _accumulatedFees[address(token)]) >= amount, "Cannot withdraw user deposited tokens or accumulated fees");
        if(address(token) == address(0)) {
            (bool success, ) = to.call{value: amount}("");
            require(success, "ETH_TRANSFER_FAILED");
        } else {
            token.safeTransfer(to, amount);
        }
    }

    function withdrawFees(address token, address to, uint256 amount) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Not admin");
        require(to != address(0), "Zero address");
        require(_accumulatedFees[token] >= amount, "Amount exeeds accumulated fees");
        
        _accumulatedFees[token] -= amount;
        IERC20(token).safeTransfer(to, amount);
    }

    function mintToken(address token, address[] calldata to, uint256[] calldata amount) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Not admin");
        require(to.length == amount.length, "Array length not equal");
        
        for (uint i = 0; i < to.length; i++) {
            require(to[i] != address(0), "Zero address");
            require(amount[i] > 0, "Zero amount");
            
            IERC20Mintable(token).mint(to[i], amount[i]);
        }
    }

    // ADMIN ROLES

    /**
     * @notice Grant role after a delay
     * @param role role hash
     * @param account assignee
     * @param validAtTimestamp timestamp at which user can accept the role, 0 will revoke pending role
     **/
    function grantRole(bytes32 role, address account, uint256 validAtTimestamp) public {
        bytes32 roleAdmin = getRoleAdmin(role);
        require(hasRole(roleAdmin, msg.sender), "AccessControl: sender must be an admin to grant");
        if (!hasRole(role, account)) {
            pendingRoles[role][account] = validAtTimestamp;
        }
    }

    /**
     * @notice Accept role after a delay
     * @param role role hash
     **/
    function acceptRole(bytes32 role) public {
        uint256 timestamp = pendingRoles[role][msg.sender];
        require(timestamp <= block.timestamp && timestamp !=0, "Role not assigned");
        _setupRole(role, msg.sender);
        pendingRoles[role][msg.sender] = 0;
    }

    function grantRole(bytes32 /*role*/, address /*account*/) public pure override {
        revert("Direct role assignment not allowed");
    }

    function _wmbReceive(
        bytes calldata data,
        bytes32 messageId,
        uint256 fromChainId,
        address /*fromSC*/
    ) internal override {
		(address to, address token, uint256 amount, Operation op) = _decodeMessageData(data);
        require(_supportedTokensForReceive[fromChainId][token] != address(0), "Unsupported token");
        require(_allowedTokenOperations[fromChainId][token] == op, "Not allowed operation");

        if (op == Operation.Withdraw) {
            _withdraw(
                to,
                IERC20(_supportedTokensForReceive[fromChainId][token]),
                amount,
                messageId
            );
        } else if (op == Operation.Mint) {
            _mint(
                payable(to),
                IERC20Mintable(_supportedTokensForReceive[fromChainId][token]),
                amount,
                messageId
            );
        } else {
            revert("Invalid operation");
        }
    }

    function _encodeMessageData(
        address to,
        address token,
        uint256 amount,
        Operation op
    ) internal pure returns(bytes memory) {
        return abi.encode(
            to,
            token,
            amount,
            op
        );
    }

    function _decodeMessageData(
        bytes memory data
    ) internal pure returns(address to, address token, uint256 amount, Operation op) {
        (to, token, amount, op) = abi.decode(data, (address, address, uint256, Operation));
    }

    /**
     * @notice Function to be called by the node group to withdraw the underlying assets from the contract
     * @param to address on chain to send underlying assets to
     * @param token ERC20 compatible token to withdraw from the bridge
     * @param amount Amount in native token decimals to withdraw
     **/
    function _withdraw(
        address to,
        IERC20 token,
        uint256 amount,
        bytes32 messageId
    ) internal nonReentrant {
        require(_balances[address(token)] >= amount, "Amount exeeds balance");
        _balances[address(token)] -= amount;
        if (address(token) == WETH_ADDRESS && WETH_ADDRESS != address(0)) {
            IWETH9(WETH_ADDRESS).withdraw(amount);
            (bool success, ) = to.call{value: amount}("");
            if (success) {
                emit TokenWithdraw(to, token, amount, 0, messageId);
            } else {
                emergencyTokenStorage[to][address(0)] += amount;
                emit TokenAddedToEmergencyTokenStorage(to, address(0), amount);
            }
        } else {
            try token.transfer(to, amount) {
                emit TokenWithdraw(to, token, amount, 0, messageId);
            } catch {
                emergencyTokenStorage[to][address(token)] += amount;
                emit TokenAddedToEmergencyTokenStorage(to, address(token), amount);
            }
        }
    }

    /**
     * @notice Nodes call this function to mint a DioneBridgeERC20 (or any asset that the bridge is given minter access to). This is called by the nodes after a TokenDeposit event is emitted.
     * @dev This means the DioneBridge.sol contract must have minter access to the token attempting to be minted
     * @param to address on other chain to redeem underlying assets to
     * @param token ERC20 compatible token to deposit into the bridge
     * @param amount Amount in native token decimals to transfer cross-chain post-fees
     **/
    function _mint(
        address payable to,
        IERC20Mintable token,
        uint256 amount,
        bytes32 messageId
    ) internal nonReentrant {
        emit TokenMint(to, token, amount, 0, messageId);
        if (address(token) == WETH_ADDRESS && WETH_ADDRESS != address(0)) {
            require(_balances[address(token)] >= amount, "Amount exeeds balance");
            _balances[address(token)] -= amount;
            IWETH9(WETH_ADDRESS).withdraw(amount);
            (bool success, ) = to.call{value: amount}("");
            if (!success) {
                emergencyTokenStorage[to][address(0)] += amount;
                emit TokenAddedToEmergencyTokenStorage(to, address(0), amount);
            }
        } else {
            uint256 balance = _balances[address(token)];
            if (balance < amount) {
                _balances[address(token)] -= balance;
                uint256 mintAmount = amount - balance;
                require(token.totalSupply() + mintAmount <= getWrappedTokenMaxSupply(address(token)) , "Exceeds max supply");
                token.mint(address(this), mintAmount);
            } else {
                _balances[address(token)] -= amount;
            }
            IERC20(token).safeTransfer(to, amount);
        }
    }

    function _sendMessage(
        uint256 toChainId,
        bytes memory msgData,
        uint256 messageFee
    ) internal {
        require(_supportedBridges[toChainId] != address(0), "Destination bridge address zero");
        _dispatchMessage(toChainId, _supportedBridges[toChainId], msgData, messageFee);
    }

    function _calcBridgeFee(uint256 amount) internal view returns(uint256) {
        return isFeeFree[msg.sender] 
            ? 0
            : amount * feeInBp / percentConverter;
    }
}
"
    },
    "contracts/bridge/interfaces/IWETH9.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity >=0.4.0;

interface IWETH9 {
    function name() external view returns (string memory);

    function symbol() external view returns (string memory);

    function decimals() external view returns (uint8);

    function balanceOf(address) external view returns (uint256);

    function allowance(address, address) external view returns (uint256);

    receive() external payable;

    function deposit() external payable;

    function withdraw(uint256 wad) external;

    function totalSupply() external view returns (uint256);

    function approve(address guy, uint256 wad) external returns (bool);

    function transfer(address dst, uint256 wad) external returns (bool);

    function transferFrom(
        address src,
        address dst,
        uint256 wad
    ) external returns (bool);
}

"
