  },
  "settings": {
    "optimizer": {
      "enabled": true,
      "runs": 20000
    },
    "evmVersion": "berlin",
    "outputSelection": {
      "*": {
        "*": [
          "evm.bytecode",
          "evm.deployedBytecode",
          "devdoc",
          "userdoc",
          "metadata",
          "abi"
        ]
      }
    },
    "metadata": {
      "useLiteralContent": true
    },
    "libraries": {}
  }
